import React, { useState } from 'react';
import './App.css';

const ExpenseForm = () => {
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!amount || !category || !date) {
      alert("Please fill out Amount, Category, and Date.");
      return;
    }

    const expenseData = {
      amount,
      category,
      description,
      date
    };

    console.log("Expense Submitted:", expenseData);

    setAmount('');
    setCategory('');
    setDescription('');
    setDate('');
  };

  return (
    <div className="form-wrapper">
      <form className="expense-form" onSubmit={handleSubmit}>
        <h2 className="form-title">‚ûï Add New Expense</h2>

        <div className="form-group">
          <label>üí∞ Amount ($)</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            placeholder="e.g. 45.99"
          />
        </div>

        <div className="form-group">
          <label>üìÇ Category</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">-- Select Category --</option>
            <option value="Food">Food</option>
            <option value="Travel">Travel</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Groceries">Groceries</option>
          </select>
        </div>

        <div className="form-group">
          <label>üìù Description</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="e.g. Lunch with friends"
          />
        </div>

        <div className="form-group">
          <label>üìÖ Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>

        <button className="submit-btn" type="submit">Submit Expense</button>
      </form>
    </div>
  );
};

export default ExpenseForm;
